import { Card } from 'semantic-ui-react';
import formatDate from '../../utils/formatDate';

const cardStyle = {
  width: "100%",
};

function CoinList({ coins }) {
  function mapLogsToItems(coins) {
    return coins.map(coin => ({
      header: coin.description,
      image: coin.mediaUrl,
      meta: `Created: ${formatDate(coin.createdAt)}`,
      color: "teal",
      fluid: "true",
      childkey: coin._id,
      href: `/coins?_id=${coin._id}`
    }));
  }
  return <Card.Group fluid itemsPerRow="3" centered style={cardStyle} items ={mapLogsToItems(coins)} />
};

export default CoinList;
