import mongoose from 'mongoose';
const {
  ObjectId,
  Number,
  String
} = mongoose.Schema.Types;
const OrderSchema = new mongoose.Schema({
  user: {
    type: ObjectId,
    ref: "User"
  },
  products: [{
    quantity: {
      type: Number,
      default: 1
    },
    product: {
      type: ObjectId,
      ref: "Product"
    },
    name: {
      type: String,
      ref: "Product"
    }
  }],
  name: {
    type: String,
    required: true
  },
  phone: {
    type: Number,
    required: true
  },
  email: {
    type: String,
    required: true
  },
  notes: {
    type: String
  },
  orderStatus: {
    type: String,
    required: true,
    default: "Open"
  },
  total: {
    type: Number,
    required: true
  },
  mediaUrl: {
    type: String
  }
}, {
  timestamps: true
});
export default mongoose.models.Order || mongoose.model("Order", OrderSchema);
