import { Header, Accordion, Label, Segment, Icon, Button, List, Card, Image } from 'semantic-ui-react';
import { useRouter } from 'next/router';
import formatDate from '../../utils/formatDate';

function statusColor() {
  if (status === "Open") {
    setStatus();
    console.log('Order status is open');
  } else {
    console.log('Status is not true');
  }
}

function AccountOrders({ orders }) {
  const router = useRouter()

  function mapOrdersToPanels(orders) {
    return orders.map(order => ({
      key: order._id,
      title: {
        content: <Label color="blue" content={formatDate(order.createdAt)}/>
      },
      content: {
        content: (
        <>
          <Card></Card>
          <List.Header as="h3">
            Total: ${order.total}
            <Label 
              content={order.email}
              icon="mail"
              basic
              horizontal
              style={{ marginLeft: '1em' }}
            />
            <Label
              floated="right" 
              color="green" 
              size="tiny">
              {order.status}
            </Label> 
          </List.Header>
          <List>
            {order.products.map(p => (
              <List.Item key={p.product._id}>
                <Image avatar src={p.product.mediaUrl}/>
                <List.Content>
                  <List.Header>{p.product.name}</List.Header>
                  <List.Description>
                    {p.quantity} . ${p.product.price}
                  </List.Description>
                </List.Content>
                <List.Content floated="right">
                  <Label color="grey" size="tiny">
                    {p.product.sku}
                  </Label> 
                </List.Content>
              </List.Item>
            ))}
          </List>
        </>
        )
      }
    }));
  }

  return <>
    <Header as="h2">
      <Icon name="folder open"/>
      Orders
    </Header>
    {orders.length === 0 ? (
      <Segment inverted tertiary color="grey" textAlign="center">
        <Header icon>
          <Icon name="copy outline"/>
          No past orders.
        </Header>
        <div>
          <Button onClick={() => router.push('/')} color="orange">
            View Products
          </Button>
        </div>
      </Segment>
    ) : (
      <Accordion
        fluid
        styled
        exclusive={false}
        panels={mapOrdersToPanels(orders)}
        />
    )}
  </>;
}

export default AccountOrders;
