import { Card } from 'semantic-ui-react'

const orderCardStyle = {
  width: "80%",
  height: "220px",
  background: "#333" 
};

function OrderList({ orders }) {
  function mapOrdersToItems(orders) {
    return orders.map(order => ({
      header: order.phone,
      //image: log.mediaUrl,
      meta: `Order Total: ${order.total} Order Items: ${order.products.product} Order Items: ${order.products.qty}`,
      color: "teal",
      fluid: `true`,
      childkey: order._id,
      href: `/order?_id=${order._id}`
    }));
  }
  return <Card.Group fluid itemsPerRow="1" centered style={orderCardStyle} items ={mapOrdersToItems(orders)} />
};

export default OrderList;
