import React from 'react';
import { 
  Form, 
  Input, 
  TextArea, 
  Button, 
  Image, 
  Message, 
  Header, 
  Icon
 } from 'semantic-ui-react';

function CreateProduct() {
  const [product, setProduct] = React.useState({
    name: "",
    price: "",
    media: "",
    description: "",
  })

  function handleChange(event) {
    const { name, value } = event.target;
    setProduct((prevState) =>({ ...prevState, [name]: value }));
    console.log(product);
  }

  return (
    <>
      <Header as="h2" block>
        <Icon name='add' color="orange" />
        Create New Product
      </Header>
      <Form>
        <Form.Group widths="equal">
          <Form.Field
            control={Input}
            name="name"
            label="Name"
            placeholder="Name"
            onChange={handleChange}
          />
          <Form.Field
            control={Input}
            name="price"
            label="Price"
            placeholder="0.00"
            min="0.00"
            step="0.01"
            type="number"
            onChange={handleChange}
          />
          <Form.Field
            control={Input}
            name="media"
            type="file"
            label="Media"
            accept="image/*"
            content="Select Image"
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Field
          control={TextArea}
          name="description"
          label="Description"
          placeholder="Description"
          onChange={handleChange}
        />
        <Form.Field
          control={Button}
          color="blue"
          icon="pencil alternate"
          content="Submit"
          type="Submit"
        />
      </Form>
    </>
  )
}

export default CreateProduct;
