import { Card } from 'semantic-ui-react';
import formatDate from '../../utils/formatDate';

const cardStyle = {
  width: "100%",
};

function CoinList({ coins }) {
  function mapCoinsToItems(coins) {
    return coins.map(coin => ({
      header: coin.description,
      image: coin.mediaUrl,
      meta: `Item: ${coin.index}`,
      color: "teal",
      fluid: "true",
      childkey: coin._id,
      href: `/coin?_id=${coin._id}`
    }));
  }
  return <Card.Group fluid itemsPerRow="3" centered style={cardStyle} items ={mapCoinsToItems(coins)} />
};

export default CoinList;
