import Log from '../../models/Log'
import connectDb from "../../utils/connectDb";

connectDb();

export default async (req, res) => {
    switch (req.method) {
        case "GET":
            await handleGetRequest(req, res);
            break;
        case "POST":
            await handlePostRequest(req, res);
            break;
        case "DELETE":
            await handleDeleteRequest(req, res);
            break;
        default:
            res.status(405).send(`Method ${req.method} not allowed`);
            break;
    }
};

async function handleGetRequest(req, res) {
    const { _id } = req.query
    const log = await Log.findOne({ _id })
    res.status(200).json(log)
}

async function handlePostRequest(req, res) {
    const { date, type, hours, amount, description, notes } = req.body
    try {
        if ( !mediaUrl || !date || !type || !hours || !amount|| !description || !notes) {
            return res.status(422).send("log missing one or more fields");
        }
        const log = await new Log({
            mediaUrl,
            date,
            type,
            hours,
            amount,
            description,
            notes
        }).save();
        res.status(201).json(log);
    } catch(error) {
        console.error(error);
        res.status(500).send("Server failed to create product.")
    }
}

async function handleDeleteRequest(req, res) {
    const { _id } = req.query
    await Log.findOneAndDelete({ _id })
    res.status(204).json({})
}
