import React from 'react';
import { 
  Form, 
  Input,
  Select,
  TextArea, 
  Button, 
  Image, 
  Message, 
  Header, 
  Icon
 } from 'semantic-ui-react';
import axios from 'axios';
import baseUrl from '../utils/baseUrl';
import catchErrors from '../utils/catchErrors';
import Router from 'next/router';
import AccountProfile from '../components/Account/AccountProfile';
import jwt from 'jsonwebtoken';
import { useRowSelect } from 'react-table';


 const logTypeOptions = [
  {
    key: 'Assembly',
    text: 'Assembly',
    value: 'Assembly',
  }, 
  {
    key: 'Research',
    text: 'Research',
    value: 'Research',
  },
  {
    key: 'Design',
    text: 'Design',
    value: 'Design',
  },
  {
    key: 'Purchase',
    text: 'Purchase',
    value: 'Purchase',
  },
  {
    key: 'Testing',
    text: 'Testing',
    value: 'Testing',
  },
 ]


function CreateLog({ user, _id }) {
  const [log, setLog] = React.useState(INITIAL_LOG);
  const [mediaPreview, setMediaPreview] = React.useState('');
  const [success, setSuccess] = React.useState(false);
  const [loading, setLoading] = React.useState(false);
  const [disabled, setDisabled] = React.useState(true);
  const [error, setError] = React.useState('');
  const [logType, setLogType] = React.useState(INITIAL_LOG.logType);
  const [amount, setAmount] = React.useState(false);

  React.useEffect(() => {
    const isLog = Object.values(log).every(el => Boolean(el))
    isLog ? setDisabled(false) : setDisabled(true);
  }, [log]);

  const INITIAL_LOG = {
    user: _id,
    media: "",  
    date: "",
    hours: "",
    amount: "",
    logType: "New",
    description: "",
    notes: "",
 };

function handleChange(event) {
  const { name, value, files } = event.target;
  if (name === "media") {
    setLog(prevState => ({ ...prevState, media: files[0] }
    ));
    setMediaPreview(window.URL.createObjectURL(files[0]));
  } else {
    setLog(prevState => ({ ...prevState, [name]: value }));
    handleType();
  }
}

function getUser() {
  // 1) Verify and get user id from token
  const { userId } = jwt.verify(req.headers.authorization, process.env.JWT_SECRET)

  const user = User.findOne({ user: userId })
}

function handleType(event) {
  if (INITIAL_LOG.logType === "Purchase") {
    setAmount(true);
    handleChange;
  } else {
    setAmount(false);
    setLogType();
    console.log(INITIAL_LOG.logType);
  }
}

async function handleImageUpload() {
  const data = new FormData()
  data.append('file', log.media)
  data.append('upload_preset', 'builders-log')
  data.append('cloud_name', 'dtan19')
  const response = await axios.post(process.env.CLOUDINARY_URL, data);
  const mediaUrl = response.data.url;
  //console.log(mediaUrl);
  return mediaUrl;
}

async function handleSubmit(event) {
  try {
    event.preventDefault();
    setLoading(true);
    setError('');
    const mediaUrl = await handleImageUpload();
    const url = `${baseUrl}/api/log`
    getUser();
    console.log(INITIAL_LOG.user);
    const { user, date, hours, amount, logType, description, notes } = log;
    const payload = { mediaUrl, user, date, hours, amount, logType, description, notes }
    const response = await axios.post(url, payload);
    //console.log({ response });
    setLog(INITIAL_LOG);
    setSuccess(true);
    const logId = response.data._id;
    Router.push(`/log?_id=${logId}`)
  } catch(error) {
    catchErrors(error, setError);
    console.log(error);
  } finally {
    setLoading(false);
  }

}

  return (
    <>
      <Header as="h3" block>
      <AccountProfile {...user}/>
        <Icon name='wrench' color="blue"/>
        Create Work Log 
      </Header>
      <p>Here is your UserId: {}</p>
      <Form loading={loading} error={Boolean(error)} success={success} onSubmit={handleSubmit} >
      <Message 
        error
        header="Oops!"
        content={error}
        />
        <Message 
        success
        icon="check"
        header="Success"
        content="Your log has been posted"
        />
        <Form.Group widths="equal">
        <Form.Field
            control={Input}
            name="media"
            type="file"
            label="Image"
            accept="image/*"
            content="Select Image"
            onChange={handleChange}
          />
        </Form.Group>
        <Image src={mediaPreview} rounded centered 
        />
        <Form.Group widths="equal">
          <Form.Field
            control={Input}
            name="userId"
            label="UserID"
            placeholder={_id}
            value={log.user}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Group widths="equal">
          <Form.Select
            selection  
            name="logType"
            label="Log Type"
            placeholder="Pick Something"
            value={log.logType}
            options={logTypeOptions}
            onChange={handleChange}
          />
          <Form.Field
            control={Input}
            name="date"
            label="Date"
            type="date"
            placeholder="Date"
            value={log.date}
            onChange={handleChange}
          />
          {amount ? (<Form.Field
            control={Input}
            name="amount"
            label="Amount"
            placeholder="0.00"
            min="0.00"
            type="number"
            value={log.amount}
            onChange={handleChange}
          />) :
          (<Form.Field
            control={Input}
            name="hours"
            label="Hours"
            placeholder="0.00"
            min="0.25"
            step="0.25"
            type="number"
            value={log.hours}
            onChange={handleChange}
          />)}
        </Form.Group>
        <Form.Field
          control={TextArea}
          name="description"
          label="Description"
          placeholder="Description"
          value={log.description}
          onChange={handleChange}
        />
        <Form.Field
          control={TextArea}
          name="notes"
          label="Notes"
          placeholder="Notes"
          value={log.notes}
          onChange={handleChange}
        />
        <Form.Field
          control={Button}
          //disabled={disabled || loading}
          color="blue"
          icon="pencil alternate"
          content="Submit"
          type="Submit"
        />
      </Form>
    </>
  )
}

export default CreateLog;
